import { Component, OnInit } from '@angular/core';
import { interval } from 'rxjs';
import { QuestionService } from 'src/app/service/question.service';

@Component({
  selector: 'question',
  templateUrl: './question.component.html',
  styleUrls: ['./question.component.css']
})
export class QuestionComponent implements OnInit {

  public name:string="";
  public questionList: any = [];
  public currentQuestion:number = 0;
  public points:number=0;
  counter=60;
  correctAnswer:number = 0;
  inCorrectAnswer:number = 0; 
  interval$:any;
  progress:string="0";
isQuizCompleted:boolean=false;
  constructor(private questionService:QuestionService) { }

  ngOnInit() {
    this.name=localStorage.getItem("name")!;
    this.getAllQuestions();
    this.startCounter();
  }

  getAllQuestions(){
    this.questionService.getQuestionJson().subscribe(res=>{
      this.questionList = res.questions;
    })
  }

  nextQuestion(){
    this.currentQuestion++;
  }

  previousQuestion(){
    this.currentQuestion--;
  }

  answer(currentQno:number,option:any){
    if(option.correct){
      this.points+=10;
      this.correctAnswer++;
      setTimeout(()=>{
        this.currentQuestion++;
        this.resetCounter();
        this.getProgressPercent();
      },1000);

    }else{
    setTimeout(()=>{
      this.currentQuestion++;
      this.inCorrectAnswer++;
      this.resetCounter();
      this.getProgressPercent();
    },1000)      

      this.points-=10;
    }
  }  
  startCounter(){
    this.interval$ = interval(1000)
    .subscribe(val=>{
      this.counter--;
      if(this.counter===0){
        this.currentQuestion++;
        this.counter=60;
        this.points-=10;
      }
    });
    setTimeout(()=>{
      this.interval$.unsubscribe();
    },600000);
  }
  stopCounter(){
    this.interval$.unsubscribe();
    this.counter=0;
  }
  resetCounter(){
    this.stopCounter();
    this.counter=60;
    this.startCounter();
  }

  resetQuiz(){
    this.resetCounter();
    this.getAllQuestions();
    this.points=0;
    this.counter=60;
    this.currentQuestion=0;
    this.progress="0";
  }

  getProgressPercent(){
    this.progress = ((this.currentQuestion/this.questionList.length)*100).toString();
    return this.progress;
  }
}
<div class="container mt-5">
  <div class="card">
   <div class="d-flex justify-content-between p-3">

    <ng-container>

    </ng-container>
    <ng-container *ngIf="isQuizCompleted">
      <div class="row d-flex justify-content-between">
        <img style="width:50%" class="img-fluid col-sm-12 mx-auto">
        <div class="result text-center col-md-6 col-sm-12">
          <h3> Tebrikler <br>Sınavı tamamladınız. <br> Sonuçlar aşağıda </h3>
          <p> Toplam Soru: {{questionList.length}}</p>
          <p> Doğru Cevap: {{correctAnswer}} </p>
          <p> Yanlış Cevap: {{inCorrectAnswer}} </p>
          <p> Puanınız: {{points} }</p>
        </div>
      </div>
    </ng-container>


  </div>
</div>
