import { Component, OnInit } from '@angular/core';
import { interval } from 'rxjs';
import { QuestionService } from 'src/app/service/question.service';

@Component({
  selector: 'question',
  templateUrl: './question.component.html',
  styleUrls: ['./question.component.css']
})
export class QuestionComponent implements OnInit {

  public name:string="";
  public questionList: any = [];
  public currentQuestion:number = 0;
  public points:number=0;
  counter=60;
  correctAnswer:number = 0;
  inCorrectAnswer:number = 0; 
  interval$:any;
  progress:string="0";
  isQuizCompleted:boolean=false;

  constructor(private questionService:QuestionService) { }

  ngOnInit() {
    this.name=localStorage.getItem("name")!;
    this.getAllQuestions();
    this.startCounter();
  }

  getAllQuestions(){
    this.questionService.getQuestionJson().subscribe(res=>{
      this.questionList = res.questions;
    })
  }

  nextQuestion(){
    this.currentQuestion++;
  }

  previousQuestion(){
    this.currentQuestion--;
  }

  answer(currentQno:number,option:any){
    if(currentQno === this.questionList.length){
      this.isQuizCompleted =true;
      this.stopCounter();
    }

    if(option.correct){
      this.points+=10;
      this.correctAnswer++;
      setTimeout(()=>{
        this.currentQuestion++;
        this.resetCounter();
        this.getProgressPercent();
      },1000);

    }else{
    setTimeout(()=>{
      this.currentQuestion++;
      this.inCorrectAnswer++;
      this.resetCounter();
      this.getProgressPercent();
    },1000)      

      this.points-=10;
    }
  }  
  startCounter(){
    this.interval$ = interval(1000)
    .subscribe(val=>{
      this.counter--;
      if(this.counter===0){
        this.currentQuestion++;
        this.counter=60;
        this.points-=10;
      }
    });
    setTimeout(()=>{
      this.interval$.unsubscribe();
    },600000);
  }
  stopCounter(){
    this.interval$.unsubscribe();
    this.counter=0;
  }
  resetCounter(){
    this.stopCounter();
    this.counter=60;
    this.startCounter();
  }

  resetQuiz(){
    this.resetCounter();
    this.getAllQuestions();
    this.points=0;
    this.counter=60;
    this.currentQuestion=0;
    this.progress="0";
  }

  getProgressPercent(){
    this.progress = ((this.currentQuestion/this.questionList.length)*100).toString();
    return this.progress;
  }
}
<div class="container md-5">
<div class="card">
  <div class="d-flex justify-content-between p-3">
   <div class="image">
     <img src="" alt="">
   </div>
   <div class="header">
     <h4 style="font-family:cursive;"> Angular & TypeScript Quiz!</h4>
     <span> Welcome {{name}}</span>
   </div>
  </div>

  <ng-container>
    <div class="d-flex justify-content-around py-3">
      <div class="score">
        <h5>{{points}} Points </h5> 
      </div>
      <div class="questions">
        <span style="font-style: italic;">Question {{currentQuestion+1}} of {{questionList.length}} </span>
      </div>
      <div class="timer">
    <h5>{{counter}} sec <i class="fa fa-clock"></i></h5>
      </div>
       </div>


       <div class="progress mb-3">
      <div class="progress-bar progress-bar-striped bg-success" role="progressbar" [ngStyle]="{'width':progress+'%'}" aria-valuenow="25" style="width: 25%" aria-valuenow="25" aria-valuemin="0" aria-valuemax="100"></div>
       </div>
       <div class="question">
        <div class="card">
          <h3> {{questionList[currentQuestion]?.questionText}}</h3>
        </div>
       </div>
       <div class="options">
        <ol *ngFor="let option of questionList[currentQuestion]?.options">
          <li (click)="answer(currentQuestion+1,option)">
            <div appChangeBg [isCorrect]="option" class="card">
              {{option.text}}
            </div>
          </li>
        </ol>
       </div>
       <div class="d-flex justify-content-between">
      <button [disabled]="currentQuestion===0" class="btn" (click)="previousQuestion()"><i class="fa text-primary fa-chevron-left fa-3x" aria-hidden="true"></i></button>
      <button class="btn" (click)="resetQuiz()"><i class="fa text-primary fa-refresh fa-3x" aria-hidden="true"></i></button>
      <button class="btn" (click)="nextQuestion()"><i class="fa text-primary fa-chevron-right fa-3x" aria-hidden="true"></i></button>
      </div>

  </ng-container>

  <ng-container *ngIf="isQuizCompleted" >
    <div class="row d-flex justify-content-between">
      <img style="width:50;" class="img-fluid col-sm-12 mx-auto">
      <div class="result text-center col-md-6 col-sm-12">
        <h3> Tebrikler!! <br> testi tamamladınız. <br> sonuçlar aşağıda yer almaktadır. </h3>
        <p> Toplam Soru: {{questionList.length}} </p>
        <p> Doğru Cevap Sayısı: {{correctAnswer}} </p>
        <p> Yanlış Cevap Sayısı: {{inCorrectAnswer}} </p>
        <p> Notunuz: {{points}} puan</p>
      </div>
    </div>
  </ng-container> 

</div> 
</div>
